import AwIcon from 'viewmodel/AwIconViewModel';

export const awPicRenderFunction = ( props ) => {
    if( props.iconId ) {
        return <AwIcon iconId={props.iconId}></AwIcon>;
    }

    if( props.source ) {
        const altText = props.alt || props.source && props.source.substring( props.source.lastIndexOf( '/' ) + 1,
            props.source.lastIndexOf( '.' )  );
        return (
            <div className='sw-pic-container'>
                <img src={props.source} className={'sw-pic ' + props.className} alt={altText} draggable='false' onDragStart={ ()=> false }></img>
            </div>
        );
    }
};
