/**
 * @deprecated afx@4.2.0.
 * @alternative none, not used anymore
 * @obsoleteIn afx@5.1.0
 * @returns {JSX.Element} react component
 */

export const AwPopupCloseButtonRenderFunction = ( props ) => {
    const { children, popupAction } = props;

    const action = ( event ) => {
        popupAction && popupAction.hide && popupAction.hide();
    };

    return <button className='aw-popup-secondaryButton' onClick={action}>{children}</button>;
};
