/**
 * @deprecated afx@4.2.0.
 * @alternative AwButton
 * @obsoleteIn afx@5.1.0
 * @returns {JSX.Element} react component
 */

export const AwPopupPrimaryButtonRenderFunction = ( props ) => {
    const { children, action } = props;

    const doit = ( event ) => {
        if( !action ) { return; }
        return action( event );
    };

    return <button className='aw-popup-primaryButton' onClick={doit}>{children}</button>;
};
