/**
 * render function for AwPropertyImage
 * @deprecated afx@4.2.0.
 * @alternative <AwIcon></AwIcon>
 * @obsoleteIn afx@5.1.0
 * @returns {JSX.Element} react component
 */
import { includeImage } from 'js/moduleLoader';

export const awPropertyImageRenderFunction = ( props ) => {
    const { name, onClickHandler, className } = props;
    if( onClickHandler ) {
        return <span className={ 'sw-aria-border ' + className} tabIndex='-1'
            role='button'
            aria-label={name}
            onKeyDown={onClickHandler}
            onClick={onClickHandler}>{includeImage( name )}</span>;
    }
    return <span className={className} >{includeImage( name )}</span>;
};
