import AwWidget from 'viewmodel/AwWidgetViewModel';

/**
 * render function for AwProperty
 * @deprecated afx@4.3.0.
 * @alternative <AwWidget>
 * @obsoleteIn afx@6.0.0
 *
 * @param {*} param0 context for render function interpolation
 * @returns {JSX.Element} react component
 */
export const awPropertyRenderFunction = ( props ) => {
    const {
        viewModel,
        ctxMin,
        actions,
        i18n,
        fields,
        grids,
        formProp,
        messages,
        ...prop
    } = props;

    const { hint } = prop;
    return <AwWidget {...prop} renderingHint={hint}></AwWidget>;
};
