import AwPropertyLabel from 'viewmodel/AwPropertyLabelViewModel';
import AwRadiobuttonVal from 'viewmodel/AwRadiobuttonValViewModel';

/**
 * render function for AwRadiobutton
 * @param {*} param0 context for render function interpolation
 * @returns {JSX.Element} react component
 */
export const awRadiobuttonRenderFunction = ( props ) => {
    const {
        viewModel,
        ctxMin,
        actions,
        i18n,
        fields,
        grids,
        formProp,
        messages,
        ...prop
    } = props;

    const { list } = prop;
    const property = prop;

    property.fielddata.renderingHint = 'radiobutton';

    return (
        <AwPropertyLabel {...property}>
            <AwRadiobuttonVal prop={property} vals={list}></AwRadiobuttonVal>
        </AwPropertyLabel>
    );
};
