export const awSplitterRenderFunction = ( props ) => {
    let classes = props.direction === 'HORIZONTAL' ? 'aw-layout-splitterLineHorizontal' : 'aw-layout-splitterLine';
    let viewMode = props.ctx.ViewModeContext ? props.ctx.ViewModeContext.ViewModeContext : null;
    return (
        <div data-locator={`aw-splitter${ props.direction ? '-'.concat( props.direction.toLowerCase() ) : ''}`} viewMode={viewMode} isPrimarySplitter={props.isPrimarySplitter} className='aw-layout-splitter' ref={props.elementRefList.get( 'splitter' )}>
            <div className={classes}></div>
        </div>
    );
};
