import AwPropertyLabel from 'viewmodel/AwPropertyLabelViewModel';
import AwTextBoxVal from 'viewmodel/AwTextBoxValViewModel';
import AwPropertyLovVal from 'viewmodel/AwPropertyLovValViewModel';

/**
 * render function for AwTextbox
 * @param {*} param0 context for render function interpolation
 * @returns {JSX.Element} react component
 */
export const awTextboxRenderFunction = ( props ) => {
    return (
        <AwPropertyLabel {...props}>
            {props.fielddata.isEditable && props.fielddata.hasLov &&  <AwPropertyLovVal {...props} ></AwPropertyLovVal>}
            {!props.fielddata.hasLov && <AwTextBoxVal {...props} />}
        </AwPropertyLabel>
    );
};
