import AwDatetime from 'viewmodel/AwDatetimeViewModel';

export const awTimeRenderFunction = ( props ) => {
    const {
        viewModel,
        ctxMin,
        actions,
        i18n,
        grids,
        formProp,
        messages,
        ...prop
    } = props;
    let fielddata = { ...prop.fielddata };

    let dateApi = { ...prop.fielddata.dateApi };
    dateApi.isDateEnabled = false;
    dateApi.isTimeEnabled = true;
    fielddata.dateApi = dateApi;
    const passedProps = { ...prop, fielddata };
    return (
        <AwDatetime {...passedProps}></AwDatetime>
    );
};
