import { noop } from 'js/declUtils';
import wcagSvc from 'js/wcagService';
/**
 * render function for AwTitleLink
 * @param {*} param0 context for render function interpolation
 * @returns {JSX.Element} react component
 */
export const awTitleLinkRenderFunction = ( props ) => {
    const { action, ...prop } = props;
    const doIt = ( event ) => {
        return action ? action( event ) : '';
    };

    const name = prop && prop.fielddata ? prop.fielddata.propertyDisplayName : '';

    const handleKeyDown = ( e ) => {
        if( wcagSvc.isValidKeyPress( e ) ) {
            doIt( e );
        }
    };

    return (
        <div className='aw-title-link'>
            <div className='aw-layout-panelSectionTitle'>
                <a
                    className='aw-aria-border link-style-5'
                    aria-label={name}
                    tabIndex='0' href={noop}
                    onKeyDown={( e )=>handleKeyDown( e )}
                    onClick={( e )=> doIt( e )}>
                    {name}
                </a>
            </div>
        </div>
    );
};
