import AwPropertyLabel from 'viewmodel/AwPropertyLabelViewModel';
import AwToggleButtonVal from 'viewmodel/AwToggleButtonValViewModel';

/**
 * render function for AwToggleButton
 * @param {*} props context for render function
 * @returns {JSX.Element} react component
 */
export const awTogglebuttonRenderFunction = ( props ) => {
    const {
        viewModel,
        ctxMin,
        actions,
        i18n,
        fields,
        grids,
        formProp,
        messages,
        ...prop
    } = props;

    prop.fielddata.renderingHint = 'togglebutton';

    return (
        <AwPropertyLabel {...prop}>
            <AwToggleButtonVal {...prop}></AwToggleButtonVal>
        </AwPropertyLabel>
    );
};
