import AwTypeIcon from 'viewmodel/AwTypeIconViewModel';

//This component is AW specific, can this be moved to AW
export const awTypeCellRenderFn = ( props ) => {
    let { item, vmo } = props;

    if( !item ) { item = vmo; }

    if( !item ) { return; }
    const value = item.props && item.props.type_name && item.props.type_name.uiValue;
    return (
        <div className='aw-tcWidgets-modelTypeCell'>
            <AwTypeIcon vmo={item}></AwTypeIcon>
            <div title={value} className='aw-tcWidgets-modelTypeCellTitle'>{value}</div>
        </div>
    );
};
