import { getUIViewComponent } from 'js/routerHelper';

/**
 * Wrapper of router UIView
 * @param {*} param0 context for render function interpolation
 * @returns {JSX.Element} react component
 */
export const uiViewRenderFunction = ( props ) => {
    const UIView = getUIViewComponent();
    const render = ( Component, childProps ) => {
        return <Component subPanelContext={ props.subPanelContext } key={childProps.key} ></Component>;
    };
    return <UIView render={render}/>;
};
