import eventBus from 'js/eventBus';

export const getNextSite = ( siteName ) => {
    let modSiteName;
    let modState;
    if( siteName === 'Ellora Caves' ) {
        modSiteName = 'Big Ben';
        modState = 'UK';
    } else {
        modSiteName = 'Ellora Caves';
        modState = 'India';
    }
    return { modSiteName, modState };
};

export const getMoreDetails = ( siteName ) => {
    let details;
    if( siteName === 'Ellora Caves' ) {
        details = `
        These 34 monasteries and temples, extending over more than 2 km, were dug side by side in the wall of a high basalt cliff,
        not far from Aurangabad, in Maharashtra. Ellora, with its uninterrupted sequence of monuments dating from A.D. 600 to 1000,
        brings the civilization of ancient India to life. Not only is the Ellora complex a unique artistic creation and a
        technological exploit but, with its sanctuaries devoted to Buddhism, Hinduism and Jainism,it illustrates the spirit of tolerance that was characteristic of ancient India.
        `;
    } else if( siteName === 'Big Ben' ) {
        details = `
        Big Ben is the nickname for the Great Bell of the striking clock at the north end of the Palace of Westminster in London; the name is frequently extended to refer to both the clock and the clock tower.
        `;
    }

    //it looks like success or failure event do not fire when actions have outputData
    eventBus.publish( 'siteSelectedEvent' );
    return { details, lessDetails: false };
};

export const getLessDetails = ( state ) => {
    return { details: null, state: state, lessDetails: true };
};

export const getState = ( state ) => {
    return state;
};

export const populateMessage = ( message ) => {
    return message;
};
