/**
 * mock for dynamic import
 *
 * @module js/componentRegistry
 *
 * @namespace componentRegistry
 */

export const isComponentRegistered = ( componentName ) => {
    var componentNames = '//SWF_COMPONENT_CHECKER_COMMENT';
    const componentNamesArray = componentNames.split( ',' );
    var component = 'viewmodel/' + componentName + 'ViewModel';
    return componentNamesArray.includes( component );
};
const exports = {
    isComponentRegistered
};
export default exports;
