/* eslint-disable react/display-name */

/**
 * @module js/awEnterHOC
 */
import React from 'react';

const displayName = 'AwEnter';

const awEnterHOC = ( Component ) => {
    // https://reactjs.org/docs/forwarding-refs.html#forwarding-refs-to-dom-components
    return React.forwardRef( ( { children, ...props }, ref ) => {
        let finalProps = { ...props };
        const handleKeyDown = ( event, awEnter ) => {
            if( event.key === 'Enter' ) {
                awEnter();
                event.preventDefault();
            }
        };
        if( typeof Component === 'object' ) {
            Component.displayName = displayName;
        }
        finalProps.onKeyDown = ( event ) => handleKeyDown( event, props.awEnter );
        return (
            <Component {...finalProps}>{children}</Component>
        );
    } );
};

export default awEnterHOC;
