/**
 * @module js/chartProviderUtils
 */
import { useState } from 'react';
import _ from 'lodash';

export const useChartProviders = ( chartProviders ) => {
    if( !_.isUndefined( chartProviders ) ) {
        const [ dataProviderNames ] = useState( () => {
            return chartProviders !== undefined ? Object.keys( chartProviders ) : [];
        } );
        dataProviderNames.forEach( ( chart ) => {
            //TODO: Logic for chartpoints
            // eslint-disable-next-line react-hooks/rules-of-hooks
            const [ chartPoints, setChartPoints ] = useState( [] );
            chartProviders[ chart ].chartPoints = chartPoints;
            chartProviders[ chart ].setChartPoints = setChartPoints;
        } );
        return chartProviders;
    }
    return undefined;
};
