export let debugConditions = function( conditionName, conditionState, conditionExp, declViewModel ) {
    var declTraceObject = {
        type: 'conditions',
        payload: {
            viewModel: declViewModel,
            conditionName: conditionName,
            expression: conditionExp,
            conditionState: conditionState
        }
    };
    if( logger.isDeclarativeLogEnabled() ) {
        logger.declarativeLog( 'DECLARATIVE TRACE - Conditions: %O ', declTraceObject );
    }
};

/**
 *
 * @param {*} arguments[0] type of the breakpoint
 *
 */
export let debug = function() {
    try {
        if( !logger.isDeclarativeLogEnabled() ) {
            return;
        }

        let brkPointType = arguments[ 0 ];

        if( breakpointSvc.hasConditionSatisfied( brkPointType, arguments[ 1 ], arguments[ 2 ], arguments[ 3 ] ) ) {
            // eslint-disable-next-line no-debugger
            debugger;
        }

        if( brkPointType === 'actions' ) {
            switch ( arguments[ 3 ] ) {
                case 'pre':
                    debugActionStart( arguments[ 4 ], arguments[ 5 ], arguments[ 6 ] );
                    break;
                case 'post':
                    debugActionEnd( arguments[ 4 ], arguments[ 5 ], arguments[ 6 ] );
                    break;
                case 'default': //do nothing
                    break;
            }
        }
        // eslint-disable-next-line no-empty
    } catch ( e ) {}
};

let printData = result => {
    result.map( ( entry ) => {
        entry.children.map( ( child ) => {
            child.handlers.map( ( handler ) => {
                logger.declarativeLog( ' ' + '~' + entry.anchor + '~' + entry.id + '~' + entry.type + '~' + child.id + '~' + handler.id + '~' + handler.activeWhen + '~' + handler
                    .visibleWhen );
            } );
        } );

        if( entry.children.length === 0 ) {
            entry.handlers.map( ( handler ) => {
                logger.declarativeLog( ' ' + '~' + entry.anchor + '~' + entry.id + '~' + entry.type + '~' + undefined + '~' + handler.id + '~' + handler.activeWhen + '~' + handler.visibleWhen );
            } );
        }
    } );
};

const procesHandler = handler => {
    let singleHandler = {};
    singleHandler.id = handler.id;
    if( handler.activeWhen ) {
        if( _.isObject( handler.activeWhen.expression ) ) {
            singleHandler.activeWhen = JSON.stringify( handler.activeWhen.expression );
        } else {
            singleHandler.activeWhen = handler.activeWhen.expression;
        }
    }
    if( handler.visibleWhen ) {
        if( _.isObject( handler.visibleWhen.expression ) ) {
            singleHandler.visibleWhen = JSON.stringify( handler.visibleWhen.expression );
        } else {
            singleHandler.visibleWhen = handler.visibleWhen.expression;
        }
    }
    return singleHandler;
};

export const reduceData = ( anchor, data ) => {
    if( logger.isDeclarativeLogEnabled() ) {
        let result = [];
        data.map( ( obj ) => {
            let singleResult = {
                anchor,
                id: '',
                type: '',
                children: [],
                handlers: []
            };
            for( let key in obj ) {
                if( key === 'id' ) {
                    singleResult.id = obj.id;
                }
                if( key === 'type' ) {
                    singleResult.type = obj.type;
                }
                if( key === 'children' ) {
                    obj.children.map( ( child ) => {
                        let singleChild = {
                            id: '',
                            handlers: []
                        };
                        singleChild.id = child.id;
                        child.handlers.map( ( handler ) => {
                            singleChild.handlers.push( procesHandler( handler ) );
                        } );
                        singleResult.children.push( singleChild );
                    } );
                }
                if( key === 'handlers' ) {
                    obj.handlers.map( ( handler ) => {
                        singleResult.handlers.push( procesHandler( handler ) );
                    } );
                }
            }
            result.push( singleResult );
        } );
        printData( result );
    }
};

export default {
    debugActionStart,
    debugEventPub,
    debugEventSub,
    debugActionEnd,
    debugMessages,
    debugLifecyleHooks,
    debugViewAndViewModel,
    debugGetCommandsForAnchor,
    debugCommandRender,
    debugUpdateHandlerOnCommand,
    debugPreProcessingDataParser,
    debugPostProcessingDataParser,
    debugDataProviderNextPage,
    debugDataProviderInitialize,
    debugConditions,
    debug,
    reduceData
};
