export const selectionSummaryRenderFunction = ( props ) => {
    const { subPanelContext, viewModel } = props;
    const { i18n } = viewModel;
    return <div className='sw-selectionSummary'>
        <div className='sw-sectionTitle sw-sectionTitleContainer'>{subPanelContext.selection.length} {i18n.selected}</div>
    </div>;
};
