import AwSplmTable from 'viewmodel/AwSplmTableViewModel';

export const contentFilter = {
    isIdOfObject: function( vmo, uid ) {
        return vmo && vmo.uid && vmo.uid === uid;
    }
};

export const tableWithContentFilterRenderFn = ( props ) => {
    props.grids.exampleTable.contentFilter = contentFilter;
    return (
        <AwSplmTable {...props.grids.exampleTable} />
    );
};
