/**
 * @module js/awWindowService
 */
export default class AwWindowService {
    constructor() {
        // To stop people doing new practice
        throw Error( `Please call '${this.constructor.name}.instance' instead of 'new ${this.constructor.name}()'` );
    }

    static get instance() {
        return window;
    }
}
