/**
 * @module js/PwaCommandBarService
 */
import { AwServerVisibilityToolbar } from 'js/AwServerVisibilityCommandBarService';
import _ from 'lodash';
export const pwaCommandBarRenderFunction = ( props ) => {
    let pwaAnchor = 'aw_primaryWorkArea';
    if( props.subPanelContext && props.subPanelContext.clientScopeURI && props.subPanelContext.clientScopeURI !== '' ) {
        pwaAnchor = pwaAnchor + ',' + 'aw_primaryWorkArea' + '_' + props.subPanelContext.clientScopeURI;
    }
    if( props.subPanelContext && props.subPanelContext.name !== '' ) {
        pwaAnchor = pwaAnchor + ',' + 'aw_primaryWorkArea' + '_' + props.subPanelContext.name;
    }
    if( props.subPanelContext && props.subPanelContext.parentStateName !== '' ) {
        pwaAnchor = pwaAnchor + ',' + 'aw_primaryWorkArea' + '_' + props.subPanelContext.parentStateName;
    }
    return (
        <AwServerVisibilityToolbar
            firstAnchor={pwaAnchor}
            secondAnchor='aw_workArea_right'
            orientation='HORIZONTAL'
            context={props.subPanelContext}
            mselected={props.mselected || _.get( props, 'subPanelContext.selectionData.selected' )}
            pselected={props.pselected ||  _.get( props, 'subPanelContext.baseSelection' )}>
        </AwServerVisibilityToolbar>
    );
};
