/**
 * @module js/AwCompare2Service
 */
import AwSplmTable from 'viewmodel/AwSplmTableViewModel';
export const awCompare2RenderFunction = ( props ) => {
    setGridOptionsForCompare( props );
    return (
        <AwSplmTable {...props} ></AwSplmTable>
    );
};

export const setGridOptionsForCompare = ( props ) => {
    if( !props.gridOptions ) {
        props.gridOptions = {};
    }
    props.gridOptions.addIconColumn = props.addIconColumn ? props.addIconColumn : false;
    props.gridOptions.transpose = true;
    props.gridOptions.enableDragAndDrop = false;
    props.gridOptions.enableHeaderIcon = props.gridOptions.enableHeaderIcon !== false;
};
