/**
 * Detect command pieces included in the module
 */
module.exports = {
    id: 'commandsViewModel',
    /**
     * Get anything that other modules could depend on
     */
    getIncluded: async( loader ) => {
        const commandsViewModel = await loader.getJson( 'commandsViewModel.json' );
        const commandActions = Object.keys( commandsViewModel.actions || {} );
        const commandConditions = Object.keys( commandsViewModel.conditions || {} );
        return {
            'command action': commandActions,
            'command condition': commandConditions
        };
    }
};
