/**
 * aw-autofocus Node Compiler
 */
const {
    Node
} = require( './utils' );

const Attr = 'aw-autofocus';

/**
 * Evaluate condition for current compiler
 * @param {Node} node input DOM Node
 * @param {context} context input context
 * @returns {boolean} true if condition matches
 */
const when = ( node ) => {
    return node.nodeType === Node.ELEMENT_NODE &&
        node.hasAttribute( Attr );
};

/**
 * Compile view input to target framework format
 * @param {Node} node input DOM Node
 * @param {context} context input context
 * @returns {Array} compile output as string array
 */
const compile = ( node, context ) => {
    let exprFocus = node.hasAttribute( Attr );
    if( exprFocus ) {
        exprFocus = node.getAttribute( Attr );
        exprFocus = exprFocus.replace( /(\r\n|\n|\r)/gm, '' ).trim();
        node.removeAttribute( Attr );
        node.setAttribute( Attr + '-hoc', `{{${exprFocus}}}` );
    }
    return context.compileFn( node, context );
};

module.exports = {
    when,
    compile
};
