/* eslint-env node */

/**
 * aw-chart element Node Compiler
 */
const {
    BaseIndent,
    Node,
    getCompName,
    evaluateAttr
} = require( './utils' );

const utils = require( '../rewired/utils' );

const attrToIgnore = [ 'chart-provider', 'chart-points' ];

/**
 * Evaluate condition for current compiler
 * @param {Node} node input DOM Node
 * @param {context} context input context
 * @returns {boolean} true if condition matches
 */
const when = ( node ) => {
    return node.nodeType === Node.ELEMENT_NODE &&
        node.nodeName === 'AW-CHART';
};

/**
 * Compile view input to target JSX
 * @param {Node} node input DOM Node
 * @param {context} context input context
 * @returns {Array} compile output as string array
 */
const compile = ( node, context ) => {
    let level = context.level;
    let indent = BaseIndent.repeat( level );
    let elem = node;
    let compName = utils.kebabCaseToPascalCase( elem.nodeName.toLowerCase() );

    let viewDesc = null;

    if( utils.getViewDep( compName ) ) {
        viewDesc = utils.getViewDep( compName ).props;
    }

    let eleName = getCompName( elem );

    let contents = [];
    let currLine = [];

    currLine.push( `${indent}<${eleName}` );

    let chartProvider = node.getAttribute( 'chart-provider' );
    let chartProviderName = chartProvider.split( '.' )[ 2 ] || undefined;

    currLine.push( `chartProvider={chartProviders.${chartProviderName}}` );

    let chartPoints = node.hasAttribute( 'chart-points' ) ? node.getAttribute( 'chart-points' ) : 'data.' + chartProviderName + '_chartPoints';
    currLine.push( `chartPoints={${chartPoints}}` );

    currLine = evaluateAttr( elem, currLine, context, viewDesc, attrToIgnore );

    contents.push( currLine.join( ' ' ) + '>' );

    contents.push( `${indent}</${eleName}>` );
    return contents;
};

module.exports = {
    when,
    compile
};
