/* eslint-env node */

/**
 * aw-check-list element Node Compiler
 */
const {
    mapChildNodes,
    mapExprToAwParse,
    BaseIndent,
    Node,
    getCompName,
    evaluateAttr
} = require( './utils' );

const utils = require( '../rewired/utils' );

const attrToIgnore = [ 'update-hook' ];

/**
 * Evaluate condition for current compiler
 * @param {Node} node input DOM Node
 * @param {context} context input context
 * @returns {boolean} true if condition matches
 */
const when = ( node ) => {
    return node.nodeType === Node.ELEMENT_NODE &&
        node.nodeName === 'AW-CHECK-LIST';
};

/**
 * Compile view input to target JSX
 * @param {Node} node input DOM Node
 * @param {context} context input context
 * @returns {Array} compile output as string array
 */
const compile = ( node, context ) => {
    // process indent
    let level = context.level;
    let indent = BaseIndent.repeat( level );

    let elem = node;

    // old way
    // <aw-check-list prop="xxx">
    //     </XXX>
    // </aw-check-list>

    // new way
    // <AwCheckList tabs= {xxx} action={actions.xxx} updateHook={ {data, dispatch, conditions} }>
    //      </XXX>
    // </AwCheckList>
    let updateHook = elem.hasAttribute( 'update-hook' ) ? elem.getAttribute( 'update-hook' ) : null;

    let compName = utils.kebabCaseToPascalCase( elem.nodeName.toLowerCase() );

    let viewDesc = null;

    if( utils.getViewDep( compName ) ) {
        viewDesc = utils.getViewDep( compName ).props;
    }

    let eleName = getCompName( elem );

    let contents = [];
    let currLine = [];

    currLine.push( `${indent}<${eleName}` );

    currLine.push( `actionDefs={${mapExprToAwParse( 'actions', context.locals )}}` );

    let hookValue = updateHook || '{data, conditions, dispatch }';
    let parsedHookVal = mapExprToAwParse( hookValue, context.locals );
    currLine.push( `updateHook={${parsedHookVal}}` );

    currLine = evaluateAttr( elem, currLine, context, viewDesc, attrToIgnore );

    contents.push( currLine.join( ' ' ) + '>' );

    // process children
    contents = contents.concat( mapChildNodes( elem, context ) );

    // end tag
    contents.push( `${indent}</${eleName}>` );

    return contents;
};

module.exports = {
    when,
    compile
};
